import React, { useState, useEffect, ChangeEvent } from "react";

interface Skills {
  [key: string]: string[];
}

interface Materials {
  escamas: number;
  huesos: number;
  sangre: number;
  zima: number;
  iode: number;
  kobaureo: number;
}

interface Plants {
  nillea: number;
  tamanei: number;
  almalexia: number;
  melius: number;
  anthemon: number;
  skellicornia: number;
}

interface Elements {
  fuego: number;
  cuerno: number;
  coral: number;
  cristal: number;
  rayo: number;
  metal: number;
  pluma: number;
  veneno: number;
  hielo: number;
}

interface Character {
  name: string;
  class: string;
  skills: Skills;
  materials: Materials;
  plants: Plants;
  elements: Elements;
  notes: string;
}

const defaultCharacter: Character = {
  name: "",
  class: "",
  skills: {
    A1: ["", ""],
    A2: ["", "", ""],
    B1: ["", ""],
    B2: ["", "", ""],
    C1: ["", ""],
    C2: ["", "", ""],
    D1: ["", ""],
    D2: ["", "", ""],
    E1: ["", ""],
    E2: ["", "", ""],
  },
  materials: {
    escamas: 0,
    huesos: 0,
    sangre: 0,
    zima: 0,
    iode: 0,
    kobaureo: 0,
  },
  plants: {
    nillea: 0,
    tamanei: 0,
    almalexia: 0,
    melius: 0,
    anthemon: 0,
    skellicornia: 0,
  },
  elements: {
    fuego: 0,
    cuerno: 0,
    coral: 0,
    cristal: 0,
    rayo: 0,
    metal: 0,
    pluma: 0,
    veneno: 0,
    hielo: 0,
  },
  notes: "",
};

const CharacterSheet: React.FC = () => {
  const [character, setCharacter] = useState<Character>(defaultCharacter);
  const [characters, setCharacters] = useState<Character[]>([]);

  useEffect(() => {
    loadCharacters();
  }, []);

  const handleInputChange = (
    section: keyof Character,
    key: string,
    value: string | number,
    index?: number
  ) => {
    if (section === "skills" && index !== undefined) {
      setCharacter((prev) => ({
        ...prev,
        skills: {
          ...prev.skills,
          [key]: prev.skills[key].map((item, i) =>
            i === index ? (value as string) : item
          ),
        },
      }));
    } else if (typeof value === "number") {
      setCharacter((prev) => ({
        ...prev,
        [section]: {
          ...(prev[section] as any),
          [key]: value,
        },
      }));
    } else {
      setCharacter((prev) => ({
        ...prev,
        [section]: value,
      }));
    }
  };

  const saveCharacter = () => {
    const newCharacters = [...characters, character];
    setCharacters(newCharacters);
    localStorage.setItem("characters", JSON.stringify(newCharacters));
  };

  const loadCharacters = () => {
    const storedCharacters = localStorage.getItem("characters");
    if (storedCharacters) {
      setCharacters(JSON.parse(storedCharacters));
    } else {
      const defaultCharacters = [
        {
          name: "Guerrero",
          class: "Fighter",
          skills: {
            A1: ["Ataque fuerte", "Ataque rápido"],
            A2: ["Defensa", "Bloqueo", "Esquiva"],
            B1: ["Liderazgo", "Motivación"],
            B2: ["Tácticas", "Estrategia", "Planificación"],
            C1: ["Intimidación", "Persuasión"],
            C2: ["Diplomacia", "Negociación", "Arbitraje"],
            D1: ["Resistencia", "Aguante"],
            D2: ["Fuerza", "Velocidad", "Agilidad"],
            E1: ["Percepción", "Atención"],
            E2: ["Concentración", "Reflejos", "Memoria"],
          },
          materials: {
            escamas: 5,
            huesos: 10,
            sangre: 2,
            zima: 3,
            iode: 4,
            kobaureo: 1,
          },
          plants: {
            nillea: 2,
            tamanei: 3,
            almalexia: 1,
            melius: 4,
            anthemon: 5,
            skellicornia: 3,
          },
          elements: {
            fuego: 2,
            cuerno: 1,
            coral: 3,
            cristal: 4,
            rayo: 5,
            metal: 2,
            pluma: 1,
            veneno: 0,
            hielo: 2,
          },
          notes: "Un guerrero fuerte y valiente.",
        },
        {
          name: "Mago",
          class: "Mage",
          skills: {
            A1: ["Bola de fuego", "Escudo mágico"],
            A2: ["Teletransporte", "Invisibilidad", "Levitar"],
            B1: ["Alquimia", "Herboristería"],
            B2: ["Invocación", "Conjuración", "Transformación"],
            C1: ["Sabiduría", "Conocimiento"],
            C2: ["Investigación", "Estudio", "Análisis"],
            D1: ["Meditación", "Concentración"],
            D2: ["Mana", "Energía", "Aura"],
            E1: ["Precisión", "Calculo"],
            E2: ["Conjuración", "Reflejos", "Intuición"],
          },
          materials: {
            escamas: 1,
            huesos: 2,
            sangre: 5,
            zima: 4,
            iode: 3,
            kobaureo: 0,
          },
          plants: {
            nillea: 4,
            tamanei: 2,
            almalexia: 5,
            melius: 1,
            anthemon: 3,
            skellicornia: 2,
          },
          elements: {
            fuego: 5,
            cuerno: 2,
            coral: 1,
            cristal: 4,
            rayo: 3,
            metal: 1,
            pluma: 0,
            veneno: 3,
            hielo: 5,
          },
          notes: "Un mago sabio y poderoso.",
        },
      ];
      setCharacters(defaultCharacters);
      localStorage.setItem("characters", JSON.stringify(defaultCharacters));
    }
  };

  const handleCharacterSelect = (index: number) => {
    setCharacter(characters[index]);
  };

  const deleteCharacter = (index: number) => {
    const newCharacters = characters.filter((_, i) => i !== index);
    setCharacters(newCharacters);
    localStorage.setItem("characters", JSON.stringify(newCharacters));
  };

  const renderSkillTree = () => (
    <div className="mb-4">
      <h3 className="font-bold mb-2">ÁRBOL DE HABILIDADES</h3>
      {Object.entries(character.skills).map(([key, values]) => (
        <div key={key} className="mb-2">
          <div className="font-semibold">{key}</div>
          <div className="flex space-x-2">
            {values.map((value, index) => (
              <input
                key={index}
                type="text"
                value={value}
                onChange={(e) =>
                  handleInputChange("skills", key, e.target.value, index)
                }
                className="w-14 p-1 border rounded text-center"
              />
            ))}
          </div>
        </div>
      ))}
    </div>
  );

  const renderSection = <T extends Record<string, number>>(title: string, items: T) => (
    <div className="mb-4">
      <h3 className="font-bold mb-2">{title.toUpperCase()}</h3>
      <div className="grid grid-cols-1 gap-2">
        {Object.entries(items).map(([key, value]) => (
          <div key={key} className="flex justify-between items-center">
            <span className="capitalize">{key}</span>
            <input
              type="number"
              value={value}
              onChange={(e: ChangeEvent<HTMLInputElement>) =>
                handleInputChange(
                  title.toLowerCase() as keyof Character,
                  key,
                  parseInt(e.target.value) || 0
                )
              }
              className="w-12 p-1 border rounded"
            />
          </div>
        ))}
      </div>
    </div>
  );

  return (
    <div className="max-w-4xl mx-auto p-4 bg-gray-100 rounded-lg shadow">
      <div className="flex justify-between mb-4">
        <input
          className="p-2 border rounded"
          placeholder="JUGADOR"
          value={character.name}
          onChange={(e) =>
            handleInputChange("name", "name", e.target.value)
          }
        />
        <div className="flex items-center space-x-2">
          <input
            className="p-2 border rounded"
            placeholder="CLASE"
            value={character.class}
            onChange={(e) =>
              handleInputChange("class", "class", e.target.value)
            }
          />
          <button
            className="p-2 bg-blue-500 text-white rounded"
            onClick={saveCharacter}
          >
            Guardar
          </button>
        </div>
      </div>

      <div className="grid grid-cols-4 gap-4">
        <div>{renderSkillTree()}</div>
        <div>{renderSection("materials", character.materials)}</div>
        <div>{renderSection("plants", character.plants)}</div>
        <div>{renderSection("elements", character.elements)}</div>
      </div>

      <div className="mt-4">
        <h3 className="font-bold mb-2">NOTAS</h3>
        <textarea
          value={character.notes}
          onChange={(e) =>
            handleInputChange("notes", "notes", e.target.value)
          }
          className="w-full p-2 border rounded"
          rows={4}
        />
      </div>

      <div className="mt-4">
        <h3 className="font-bold mb-2">Personajes Guardados</h3>
        <ul className="list-disc pl-5">
          {characters.map((char, index) => (
            <li
              key={index}
              className="cursor-pointer mb-1 p-2 rounded hover:bg-gray-200 flex justify-between items-center"
              onClick={() => handleCharacterSelect(index)}
            >
              <span>{char.name} - {char.class}</span>
              <button
                className="ml-4 p-1 bg-red-500 text-white rounded"
                onClick={(e) => {
                  e.stopPropagation();
                  deleteCharacter(index);
                }}
              >
                Eliminar
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default CharacterSheet;
