import React, { useState, CSSProperties } from "react";

const MonsterLifeTracker = () => {
  const [players, setPlayers] = useState(2); // Número de jugadores
  const [sections, setSections] = useState(
    Array(10)
      .fill(0)
      .map(() => ({ life: 0, multiplier: 1 }))
  );
  const [activeSection, setActiveSection] = useState(9); // Empezar desde la derecha
  const [damage, setDamage] = useState(0); // Daño a aplicar

  const handleMultiplierChange = (index: number, value: number) => {
    const newSections = [...sections];
    newSections[index].multiplier = value;
    newSections[index].life = players * value; // Recalcular la vida inicial
    setSections(newSections);
  };

  const decreaseLife = (amount = 1) => {
    const newSections = [...sections];
    let remainingAmount = amount;

    for (let i = activeSection; i >= 0; i--) {
      if (newSections[i].life > 0) {
        if (newSections[i].life >= remainingAmount) {
          newSections[i].life -= remainingAmount;
          remainingAmount = 0;
          setActiveSection(i);
          break;
        } else {
          remainingAmount -= newSections[i].life;
          newSections[i].life = 0;
        }
      }
    }
    setSections(newSections);
  };

  const increaseLife = (amount = 1) => {
    const newSections = [...sections];
    let remainingAmount = amount;

    for (let i = activeSection; i < sections.length; i++) {
      const maxLife = players * newSections[i].multiplier;
      if (newSections[i].life < maxLife) {
        const availableSpace = maxLife - newSections[i].life;
        if (availableSpace >= remainingAmount) {
          newSections[i].life += remainingAmount;
          remainingAmount = 0;
          setActiveSection(i);
          break;
        } else {
          newSections[i].life = maxLife;
          remainingAmount -= availableSpace;
        }
      }
    }
    setSections(newSections);
  };

  const handleDamageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setDamage(Number(e.target.value));
  };

  const applyDamage = () => {
    decreaseLife(damage);
  };

  const activateSection = (index: number) => {
    setActiveSection(index);
  };

  return (
    <div style={styles.container}>
      <div style={styles.control}>
        <label>Número de Jugadores:</label>
        <select
          value={players}
          onChange={(e) => setPlayers(Number(e.target.value))}
        >
          <option value={2}>2</option>
          <option value={3}>3</option>
          <option value={4}>4</option>
        </select>
      </div>
      <div style={styles.sections}>
        {sections.map((section, index) => (
          <div key={index} style={styles.section}>
            <input
              type="number"
              value={section.multiplier}
              onChange={(e) =>
                handleMultiplierChange(index, Number(e.target.value))
              }
              style={styles.multiplier}
              min="1"
              step="1"
            />
            <div style={styles.life} onClick={() => activateSection(index)}>
              {section.life}/{players * section.multiplier}
            </div>
          </div>
        ))}
      </div>
      <div style={styles.globalControls}>
        <button style={styles.button} onClick={() => increaseLife(1)}>
          +1
        </button>
        <button style={styles.button} onClick={() => decreaseLife(1)}>
          -1
        </button>
        <button style={styles.button} onClick={() => increaseLife(10)}>
          +10
        </button>
        <button style={styles.button} onClick={() => decreaseLife(10)}>
          -10
        </button>
      </div>
      <div style={styles.damageControl}>
        <input
          type="number"
          value={damage}
          onChange={handleDamageChange}
          style={styles.damageInput}
          min="0"
        />
        <button style={styles.button} onClick={applyDamage}>
          Aplicar Daño
        </button>
      </div>
    </div>
  );
};

const styles: { [key: string]: CSSProperties } = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    height: "100vh",
  },
  control: {
    margin: "1rem 0",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  sections: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
  },
  section: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    margin: "0.5rem",
    cursor: "pointer",
  },
  life: {
    fontSize: "1.5rem",
    margin: "0.5rem 0",
  },
  globalControls: {
    display: "flex",
    marginTop: "1rem",
  },
  button: {
    fontSize: "1.5rem",
    padding: "0.5rem 1rem",
    cursor: "pointer",
    margin: "0 0.5rem",
  },
  multiplier: {
    width: "3rem",
    textAlign: "center",
  },
  damageControl: {
    display: "flex",
    alignItems: "center",
    marginTop: "1rem",
  },
  damageInput: {
    width: "4rem",
    fontSize: "1.5rem",
    textAlign: "center",
    marginRight: "0.5rem",
  },
};

export default MonsterLifeTracker;
